/**
 * Plays a sound from a Base64-encoded data URL.
 * @param dataUrl The data URL of the sound to play.
 */
const playSound = (dataUrl: string) => {
  const audio = new Audio(dataUrl);
  audio.play().catch(error => {
    // Autoplay can be blocked by the browser, so we log this gracefully
    // without interrupting the user experience.
    console.error("Audio cue playback failed:", error);
  });
};

// A short, high-pitched "blip" to indicate the mic is active.
const START_CUE = 'data:audio/wav;base64,';

// A slightly lower-pitched, softer "bloop" for stopping.
const STOP_CUE = 'data:audio/wav;base64,-';

// A subtle, low-frequency hum to indicate processing/thinking.
const PROCESSING_CUE = 'data:audio/wav;base64,-';

// A clear, sharp "plink" sound for error states.
const ERROR_CUE = 'data:audio/wav;base64,-';

export const playStart = () => playSound(START_CUE);
export const playStop = () => playSound(STOP_CUE);
export const playProcessing = () => playSound(PROCESSING_CUE);
export const playError = () => playSound(ERROR_CUE);
